const agendamentos = msg.payload;
const agora = new Date();

const dataAtual = agora.toISOString().split("T")[0];
const horaAtual = agora.toTimeString().slice(0, 5);

for (const id in agendamentos) {
    const item = agendamentos[id];
    if (
        item.status === "Ativo" &&
        item.specificDate === dataAtual &&
        item.time === horaAtual
    ) {
        // Se hor√°rio bater, envia comando MQTT
        return {
            topic: "pico/irrigacao",
            payload: "ON"
        };
    }
}
